DROP TABLE IF EXISTS `phpshop_modules_elastic_system`;CREATE TABLE IF NOT EXISTS `phpshop_modules_elastic_system` (    `id` int(11) NOT NULL auto_increment,    `api` varchar(255) NOT NULL default '',    `search_page_row` enum('1', '2', '3', '4', '5') DEFAULT '4',    `search_page_size` int(11) DEFAULT 15,    `search_show_informer_string` enum('0','1') NOT NULL default '1',    `ajax_search_categories` enum('0','1') NOT NULL default '1',    `available_sort` enum('0','1') NOT NULL default '1',    `ajax_search_products_cnt` int(11) DEFAULT 5,    `filter_show_counts` enum('0','1') NOT NULL default '0',    `filter_update` enum('0','1','2') NOT NULL default '0',    `find_in_categories` enum('0','1','2') NOT NULL default '0',    `synonyms` text default '',    `search_filter` varchar(255) NOT NULL default '',    `ajax_search_filter` varchar(255) NOT NULL default '',    `version` varchar(64) DEFAULT '1.2',    PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=cp1251;INSERT INTO `phpshop_modules_elastic_system` VALUES (1, '', '4', 15, '1', '1', '1', 5, '0', '0', '1', '', '', '', '1.2');ALTER TABLE `phpshop_products` ADD `elastic_id` varchar(64) default '';ALTER TABLE `phpshop_categories` ADD `elastic_category_id` varchar(64) default '';